mapper.select:
select * from t_student
where 1=1

	and name like #{name}


	and gradeId=#{gradeId}

	select * from t_student
    
    	
    		where gradeId=#{gradeId}
    	
    	
    		where name like #{name}
    	
    	
    		where age=#{age}

    select * from t_student
	
	
		age = #{age}
	
	
		and name like #{name}
	
	
		and gradeId=#{gradeId}

    select * from t_student
	
	
		age = #{age}
	
	
		and name like #{name}
	
	
		and gradeId=#{gradeId}

    select * from t_student
    where gradeId in 
	
		#{gradeId}

mapper.update:
update t_student

	
		age=#{age},
	
	
		name=#{name}
	

where id=#{id}
